import axios from 'axios';
import React from 'react'
import UserContext from '../../UserContext';
import './Pages.css';

class Users extends React.Component {
    constructor(props){
        super(props);
        this.state = 
        {
            Coach: [],
            Sportsman: [],
        }
    }

    FetchCoaches() {
        axios.get("http://localhost:8080/coach/getCoach")
        .then((res) => {
            this.setState({
                Coach: res.data
            })
        });
    }

    FetchSportsmans() {
        axios.get("http://localhost:8080/sportsman/getSportsman")
        .then((res) => {
            this.setState({
                Sportsman: res.data
            })
        });
    }

    componentDidMount(){
        this.FetchCoaches();
        this.FetchSportsmans();
    }

    render() {
    const {user, isAuthenticated, LogIn, LogOut} = this.context;
    return (
        <div className='users'>
            <div className='users-container'>
                <h1>Coaches:</h1>
                <br/>
                {this.state.Coach.map(coach => (
                    <>
                    <div>&emsp;<b>Name:</b>{coach['firstName']} {coach['lastName']}&emsp;User Name: {coach['userName']}&emsp;
                    Email: {coach['email']}&emsp;
                    License Number: {coach['licenseNumber']}&emsp;
                    Work Place ID: {coach['workPlaceId']}</div>
                    <br/>
                    </>
                ))}
                <h1>Sportsmans:</h1>
                <br/>
                {this.state.Sportsman.map(sportsman => (
                    <>
                    <div>Name: {sportsman['firstName']} {sportsman['lastName']}</div>
                    <div>User Name: {sportsman['userName']}</div>
                    <div>Email: {sportsman['email']}</div>
                    <br/>
                    </>
                ))}
            </div>
        </div>
    )
    }
}

Users.contextType = UserContext

export default Users
