import React from "react";
import './Form.css';
import axios from "axios";

class LoginForm extends React.Component {

    constructor(props){
        super(props);
        this.state = 
        {
            Admin: [],
            Coach: [],
            Sportsman: [],
            userType: '',
            userName: '',
            password: '',
            login: false
        }
    }

    // componentDidMount()
    // {
    //     axios.get("http://localhost:8080/login")
    //     .then((res)=>{
    //         this.setState({
    //             Admin: [],
    //             Coach: [],
    //             Sportsman: [],
    //             userType: '',
    //             userName: '',
    //             password: '',
    //             login: false
    //         })
    //     })
    // }

    submit(event,userName,userType){
        event.preventDefault();
        if(!(userName==='' && userType==='')){
            this.setState({
                userType:this.state.userType,
                userName:this.state.userName,
                password:this.state.password,})
                // .then(()=>{
                // this.componentDidMount();
                if(userType==="Admin")
                {
                    axios.get("http://localhost:8080/admin/getAdmins")
                    .then(res => {
                        this.setState({
                            Admin:res.data
                        })
                    })
                    this.state.Admin.forEach(element => {
                        if(element.userName==this.state.userName && 
                            element.password==this.state.password)
                            this.setState({login:true})
                            console.log(this.state.login)
                    });
                }
                else if(userType==="Coach")
                {
                    axios.get("http://localhost:8080/coach/getCoaches")
                    .then(res => {
                        this.setState({
                            Coach:res.data
                        })
                    })
                    this.state.Coach.forEach(element => {
                        if(element.userName==this.state.userName && 
                            element.password==this.state.password)
                            this.setState({login:true})
                    });
                }
                else if(userType==="Sportsman")
                {
                    axios.get("http://localhost:8080/sportsman/getSportsmans")
                    .then(res => {
                        this.setState({
                            Sportsman:res.data
                        })
                    })
                    this.state.Sportsman.forEach(element => {
                        if(element.userName==this.state.userName && 
                            element.password==this.state.password)
                            this.setState({login:true})
                    });
                }
            // });
        }
    }

    render(){
        return (
            <div className='form-content'>
            <form className="form" onSubmit={(e)=>this.submit(e,this.state.userName)}>
                <div className="logImg" />
                <h1>
                   Hey! Login to GoForFit
                </h1>
                <div className="form-inputs">
                    <label htmlFor="usertype" 
                    className="form-label">
                        User Type
                    </label>
                    <select
                    value={this.state.userType} onChange={(e)=>this.setState({userType:e.target.value})} type="text"
                        id='usertype'
                         name='usertype'
                         className='form-input'
                         placeholder="Choose Type"
                    >
                        <option value={0}>Admin</option>
                        <option value={1}>Coach</option>
                        <option value={2}>Sportsman</option>
                    </select>
                    </div>
                <div className="form-inputs">
                    <label htmlFor="username" 
                    className="form-label">
                        Username
                    </label>
                    <input
                    value={this.state.userName} onChange={(e)=>this.setState({userName:e.target.value})} type="text"
                        id='username'
                         name='username'
                         className='form-input'
                         placeholder="Enter your username"
                    />
                </div>
                <div className="form-inputs">
                    <label htmlFor="password" 
                    className="form-label">
                        Password
                    </label>
                        <input
                        value={this.state.password} onChange={(e)=>this.setState({password:e.target.value})} type="password"
                        id='password'
                         name='password'
                         className='form-input'
                         placeholder="Enter your password"
                    />  
                </div>                       
                <button className="form-input-btn"
                    type='submit'>
                    Login
                </button>
            </form>
            </div>
        );
    }
}

export default LoginForm;
