import axios from 'axios';
import React from 'react'
import { Navigate } from 'react-router-dom';
import UserContext from '../../UserContext';
import './Pages.css';

class EditSports extends React.Component {
    constructor(props){
        super(props);
        this.state = 
        {
            refresh: false,
            Sports: [],
        }
    }

    async deleteSport(name)
    {
        await axios.delete(`http://localhost:8080/sports/deleteSport/${name}`);
        this.setState({refresh: true});
    }

    async FetchSports() {
        await axios.get("http://localhost:8080/sports/getSports")
        .then((res) => {
            this.setState({
                Sports: res.data
            })
        });
    }

    componentDidMount(){
        this.FetchSports();
    }

    render() {
    const {user, isAuthenticated, LogIn, LogOut} = this.context;
    return (
        <div className='sports'>
            {this.state.refresh && (<Navigate to="/editSports" replace={true} />)}
            <h1>System Sports:</h1>
            <br/>
            <div className='sports-container' align="center">
                <table border="1" >
                    <tr>
                        <th><b>Name</b></th>
                        <th><b>Description</b></th>
                        <th><b>Coach</b></th>
                        <th><b>Options</b></th>
                    </tr>
                        {this.state.Sports.map(sport => (
                        <>
                        <tr>
                        <th>{sport['name']}</th>
                        <th>{sport['description']}</th>
                        <th>{sport['coach']}</th>
                        <th><button>Edit</button> <button onClick={() => this.deleteSport(sport['name'])}>Delete</button></th>
                        </tr>
                        </> ))}
                </table>
                <br/>
                <br/>
                <br/>

                <button >Add Sports</button>
            </div>

        </div>
        

    )
    }
}

EditSports.contextType = UserContext

export default EditSports
