import React from "react";
import './Form.css';
import axios from "axios";

class LoginForm extends React.Component {

    constructor(props){
        super(props);
        this.state = 
        {
            User: [],
            userType: 'Admin',
            userName: '',
            password: '',
            errMsg: ''
        }
    }

    submit(event,userName,password,userType) {
        event.preventDefault();
        if(!(userName==='')){
            console.log(userType);
            console.log(userName);
            console.log(password);
                if(userType==="Admin")
                {
                    axios.get(`http://localhost:8080/admin/auth/${userName}/${password}`)
                    .then((res) => {
                        this.setState({
                            User: res.data
                        })
                        console.log(this.state.User);
                    }).catch((error) => {
                        "Error!"
                    });
                }
                else if(userType==="Coach")
                {
                    axios.get(`http://localhost:8080/coach/auth/${userName}/${password}`)
                    .then((res) => {
                        this.setState({
                            User: res.data
                        })
                        console.log(this.state.User);
                    }).catch((error) => {
                        "Error!"
                    });
                }
                else if(userType==="Sportsman")
                {
                    axios.get(`http://localhost:8080/sportsman/auth/${userName}/${password}`)
                    .then((res) => {
                        this.setState({
                            User: res.data
                        })
                        console.log(this.state.User);
                    }).catch((error) => {
                        "Error!"
                    });
                }
                if(!this.state.User)
                {
                    this.setState({errMsg: "User does not exsits! please try again"})
                }
        }
    }

    render(){
        return (
            <div className='form-content'>
            <form className="form" onSubmit={(e)=>this.submit(e,this.state.userName,this.state.password,this.state.userType)}>
                <div className="logImg" />
                <h1>
                   Hey! Login to GoForFit
                </h1>
                <div className="form-inputs">
                    <label htmlFor="usertype" 
                    className="form-label">
                        User Type
                    </label>
                    <select
                    value={this.state.userType} onChange={(e)=>this.setState({userType:e.target.value,
                    errMsg:''})} type="number"
                        id='usertype'
                         name='usertype'
                         className='form-input'
                         placeholder="Choose Type"
                    >
                        <option value="Admin">Admin</option>
                        <option value="Coach">Coach</option>
                        <option value="Sportsman">Sportsman</option>
                    </select>
                    </div>
                <div className="form-inputs">
                    <label htmlFor="username" 
                    className="form-label">
                        Username
                    </label>
                    <input
                    value={this.state.userName} onChange={(e)=>this.setState({userName:e.target.value,
                        errMsg:''})} type="text"
                        id='username'
                         name='username'
                         className='form-input'
                         placeholder="Enter your username"
                    />
                </div>
                <div className="form-inputs">
                    <label htmlFor="password" 
                    className="form-label">
                        Password
                    </label>
                        <input
                        value={this.state.password} onChange={(e)=>this.setState({password:e.target.value,
                        errMsg:''})} type="password"
                        id='password'
                         name='password'
                         className='form-input'
                         placeholder="Enter your password"
                    />  
                </div>                       
                <button className="form-input-btn"
                    type='submit'>
                    Login
                </button>
            </form>
            </div>
        );
    }
}

export default LoginForm;
