import axios from 'axios';
import React from 'react'
import UserContext from '../../UserContext';
import './Pages.css';

class Users extends React.Component {
    constructor(props){
        super(props);
        this.state = 
        {
            Coach: [],
            Sportsman: [],
        }
    }

    FetchCoaches() {
        axios.get("http://localhost:8080/coach/getCoach")
        .then((res) => {
            this.setState({
                Coach: res.data
            })
        });
    }

    FetchSportsmans() {
        axios.get("http://localhost:8080/sportsman/getSportsman")
        .then((res) => {
            this.setState({
                Sportsman: res.data
            })
        });
    }

    componentDidMount(){
        this.FetchCoaches();
        this.FetchSportsmans();
    }

    render() {
    const {user, isAuthenticated, LogIn, LogOut} = this.context;
    return (
        <div className='users'>
            <h1 align="center"><u>System Users:</u></h1>
            <br/>
            <div className='users-container' align="center">
                <h2 align="center">Coaches:</h2>
                <br/>
                <table border="1">
                    <tr>
                        <th>Name:</th>
                        <th>User Name:  </th>
                        <th>Email:</th>
                        <th>License Number:</th>
                        <th>Work Place ID:</th>

                    </tr>
                    <tr>
                    {this.state.Coach.map(coach => (
                    <>
                    <div>&emsp;<b>Name: </b>{coach['firstName']} {coach['lastName']}&emsp;<b>User Name: </b>{coach['userName']}&emsp;
                    <b>Email: </b>{coach['email']}&emsp;
                    <b>License Number: </b>{coach['licenseNumber']}&emsp;
                    <b>Work Place ID: </b>{coach['workPlaceId']}</div>
                    <br/>
                    </>
                ))}

                    </tr>





                </table>









{/* 

