import React from "react";
import './Form.css';
import axios from "axios";

class LoginForm extends React.Component {

    constructor(props){
        super(props);
        this.state = 
        {
            Admin: [],
            Coach: [],
            Sportsman: [],
            userName: '',
            password: '',
        }
    }

    componentDidMount()
    {
        axios.get("http://localhost:8080/login")
        .then((res)=>{
            this.setState({
                Admin: [],
                Coach: [],
                Sportsman: [],
                userName: '',
                password: '',
            })
        })
        this.getUsers();
    }

    getUsers = async() => {
        await axios.get("http://localhost:8080/admin/getAdmins")
        .then(res => {
            this.setState({
                Admin:res.data
            })
        })
        await axios.get("http://localhost:8080/coach/getCoaches")
        .then(res => {
            this.setState({
                Coach:res.data
            })
        })
        await axios.get("http://localhost:8080/sportsman/getSportsmans")
        .then(res => {
            this.setState({
                Sportsman:res.data
            })
        })
    }

    // submit(event,userName){
    //     event.preventDefault();
    //     if(!(userName==='')){
    //         axios.post("http://localhost:8080/coach/sign_up",{
    //             userName:this.state.userName,
    //             email:this.state.email,
    //             password:this.state.password,
    //             firstName:this.state.firstName,
    //             lastName:this.state.lastName,
    //             year:this.state.year,
    //             month:this.state.month,
    //             day:this.state.day,
    //             height:this.state.height,
    //             weight:this.state.weight,
    //             phoneNumber:this.state.phoneNumber,
    //             licenseNumber:this.state.licenseNumber,
    //             workPlaceId:this.state.workPlaceId,
    //             sportKind:this.state.sportKind
    //         }).then(()=>{
    //             this.componentDidMount();
    //         });
    //     }
    // }

    render(){
        return (
            <div className='form-content'>
            <form className="form" onSubmit={(e)=>this.submit(e,this.state.userName)}>
                <div className="logImg" />
                <h1>
                   Hey! Login to GoForFit
                </h1>
                <div className="form-inputs">
                    <label htmlFor="usertype" 
                    className="form-label">
                        User Type
                    </label>
                    <select
                    value={this.state.userName} onChange={(e)=>this.setState({userName:e.target.value})} type="text"
                        id='usertype'
                         name='usertype'
                         className='form-input'
                         placeholder="Choose Type"
                    >
                        <option value={0}>Admin</option>
                        <option value={1}>Coach</option>
                        <option value={2}>Sportsman</option>
                    </select>
                    </div>
                <div className="form-inputs">
                    <label htmlFor="username" 
                    className="form-label">
                        Username
                    </label>
                    <input
                    value={this.state.userName} onChange={(e)=>this.setState({userName:e.target.value})} type="text"
                        id='username'
                         name='username'
                         className='form-input'
                         placeholder="Enter your username"
                    />
                </div>
                <div className="form-inputs">
                    <label htmlFor="password" 
                    className="form-label">
                        Password
                    </label>
                        <input
                        value={this.state.password} onChange={(e)=>this.setState({password:e.target.value})} type="password"
                        id='password'
                         name='password'
                         className='form-input'
                         placeholder="Enter your password"
                    />  
                </div>                       
                <button className="form-input-btn"
                    type='submit'>
                    Login
                </button>
            </form>
            </div>
        );
    }
}

export default LoginForm;
